    // 将 s 进行 BASE64 编码
    public static String encode(byte[] s) {

        return (new BASE64Encoder()).encode(s);
    }

    // 将 BASE64 编码的字符串 s 进行解码
    public static String getFromBASE64(String s) {
        if (TextUtils.isEmpty(s))
            return null;
        BASE64Decoder decoder = new BASE64Decoder();
        try {
            byte[] b = decoder.decodeBuffer(s);
            return new String(b);
        } catch (Exception e) {
            return null;
        }
    }

    // 将 BASE64 编码的字符串 s 进行解码
    public static byte[] decode(String s) {
        if (TextUtils.isEmpty(s))
            return null;
        BASE64Decoder decoder = new BASE64Decoder();
        try {
            byte[] b = decoder.decodeBuffer(s);
            return b;
        } catch (Exception e) {
            return null;
        }
    }

    public static boolean GenerateImage(String imgStr, String imgFilePath) {// 对字节数组字符串进行Base64解码并生成图片
        //byte[] bytes=DatatypeConverter.parseBase64Binary(imgStr);
        // byte[] newbytes=imgStr.getBytes("ASCII");
        //String result+=new String(bytes, "ASCII");
        // result=DatatypeConverter.printBase64Binary(bytes);
        if (imgStr == null) // 图像数据为空
            return false;
        BASE64Decoder decoder = new BASE64Decoder();

        try {
            // Base64解码
            byte[] bytes = decoder.decodeBuffer(imgStr);
//	            for (int i = 0; i < bytes.length; ++i) {
//	                if (bytes[i] < 0) {// 调整异常数据
//	                    bytes[i] += 256;
//	                }
//	            }
            // 生成jpeg图片
            OutputStream out = new FileOutputStream(imgFilePath);
            out.write(bytes);
            out.flush();
            out.close();
            return true;
        } catch (Exception e) {
            return false;
        }
    }

    public static File getTempFile(String decoderBytes) {
        BASE64Decoder decoder = new BASE64Decoder();
        try {
            byte[] b = decoder.decodeBuffer(decoderBytes);
            File f = null;
            // creates temporary file
            f = File.createTempFile("tmp", ".jpg");
            // prints absolute path
            //生成jpeg图片
            //String imgFilePath = "d://222.jpg";//新生成的图片
            FileOutputStream out = new FileOutputStream(f.getAbsoluteFile());
            out.write(b);
            out.flush();
            out.close();
            // deletes file when the virtual machine terminate
            f.deleteOnExit();
            return f;

        } catch (Exception e) {
            // if any error occurs
            e.printStackTrace();
        }
        return null;
    }
